import React, { Component } from 'react';
import web3 from './web3';
import ipfs from './ipfs';
import storehash from './storehash';
import { Button } from 'reactstrap';

class App extends Component {

state = {
      ipfsHash:null,
      buffer:'',
      ethAddress:'',
      transactionHash:'',
      txReceipt: '',
      add_rec:'',
      issuee_data_1: [],
      issuee_data_2: []
    };

recipientChange = (event) => {
  this.setState({add_rec: event.target.value});
}

//Take file input from user
captureFile =(event) => {
        event.stopPropagation()
        event.preventDefault()
        const file = event.target.files[0]
        let reader = new window.FileReader()
        reader.readAsArrayBuffer(file)
        reader.onloadend = () => this.convertToBuffer(reader)
      };

//Convert the file to buffer to store on IPFS
 convertToBuffer = async(reader) => {
      //file is converted to a buffer for upload to IPFS
        const buffer = await Buffer.from(reader.result);
      //set this buffer-using es6 syntax
        this.setState({buffer});
    };

//ES6 async function
onClick = async () => {
try{
        this.setState({blockNumber:"waiting.."});
        this.setState({gasUsed:"waiting..."});

await web3.eth.getTransactionReceipt(this.state.transactionHash, (err, txReceipt)=>{
          console.log(err,txReceipt);
          this.setState({txReceipt});
        });
      }
catch(error){
      console.log(error);
    }
}

onSubmit = async (event) => {
      event.preventDefault();

//bring in user's metamask account address
      const accounts = await web3.eth.getAccounts();
    //obtain contract address from storehash.js
      const ethAddress= await storehash.options.address;
      this.setState({ethAddress});
    //save document to IPFS,return its hash#, and set hash# to state
      await ipfs.add(this.state.buffer, (err, ipfsHash) => {
        console.log(err,ipfsHash);
        //setState by setting ipfsHash to ipfsHash[0].hash
        this.setState({ ipfsHash:ipfsHash[0].hash });
        //var add_rec= $('#add_rec').val();

        // call Ethereum contract method "sendHash" and .send IPFS hash to etheruem contract
        //return the transaction hash from the ethereum contract
        storehash.methods.issue_cert(this.state.add_rec,this.state.ipfsHash).send({
          from: accounts[0]
        }, (error, transactionHash) => {
          console.log(transactionHash);
          this.setState({transactionHash});
        });
      })
    };

    show_cert_2 = async (event) => {
      //  App.contracts.Certificate.deployed().then(function(instance) {
      // certificate_instance = instance;
      // return instance.issuee_cert_cnt(App.account,{from:App.account});
      const accounts = await web3.eth.getAccounts();
      storehash.methods.issuee_cert_cnt(accounts[0]).call({from: accounts[0]},(error, result) => {
        const len = parseInt(result);
        for(let i =0;i<len;i++){
          var i_data1=[];
          var i_data2=[];
        storehash.methods.get_cert_issuee(i).call({from: accounts[0]}, (error, ans) => {
          i_data1.push(ans[0]);
          i_data2.push(ans[1]);
          // console.log(i_data1[0]);
          // console.log(i_data2[0]);
        })
        // console.log(i_data1);
        this.setState({issuee_data_1:i_data1});
        this.state.issuee_data_1.map(item=>(console.log(item)));
        // console.log(x[0]);
        this.setState({issuee_data_2:i_data2});

        var cert_table = this.refs.cert_table_1;
        // var cert_table = $('#cert_table');                
      cert_table.empty();
      cert_table.append("<tr><th>Recipient Address</th><th> Certificate Hash</th></tr>");
      // for(var i=0;i<count;i++){
      //   certificate_instance.get_cert_issuer(i).then(function(issuer_data){
      //     var address = issuer_data[0];
      //     var hash = issuer_data[1]; 
      //     var temp = "<tr><td>"+address+"</td><td>"+hash+"</td></tr>";
      //     cert_table.append(temp);
      //   });
      // }
      }
      });
    // }).then(function(count){
    //   var cert_table = $('#cert_table'); 
    //   cert_table.empty(); 
    //   cert_table.append("<tr><th>Recipient Address</th><th> Certificate Hash</th></tr>");
    //   for(var i=0;i<count;i++){
    //     certificate_instance.get_cert_issuee(i).then(function(issuee_data){
    //       var address = issuee_data[0];
    //       var hash = issuee_data[1]; 
    //       var temp = "<tr><td>"+address+"</td><td>"+hash+"</td></tr>";
    //       cert_table.append(temp);
    //     });
    //   }
    // });
  };
// createTable=()=>{

//   let table=[];
//   let table_issue_1 = this.state.issuee_data_1;
//   let table_issue_2 = this.state.issuee_data_2;
//   for(let i =0;i<1;i++){
//     let table_1 = [];
//     // console.log(this.state.issuee_data_1);
//     table_1.push(<td>{table_issue_1[i]}</td>);
//     table_1.push(<td>{table_issue_2[i]}</td>);
//     table.push(<tr>{table_1}</tr>);
//   }
// };

render() {

return (
        <div>
        <div className="App">
          <header className="App-header">
            <h1>Ethereum and IPFS using Infura</h1>
          </header>

<hr/>
<grid>
          <h3> Choose file to send to IPFS </h3>
          <form onSubmit={this.onSubmit}>
          <label>recipient address:
            <input
              type = "text" id="add_rec" onChange = {this.recipientChange}
            />
            </label>
            <br></br>
            <input
              type = "file"
              onChange = {this.captureFile}
            />
             <Button
             bsStyle="primary"
             type="submit">
             Send it
             </Button>
          </form>
<hr/>
 <Button onClick = {this.onClick}> Get Transaction Receipt </Button>
 <hr/>
  <table bordered responsive>
                <thead>
                  <tr>
                    <th>Tx Receipt Category</th>
                    <th> </th>
                    <th>Values</th>
                  </tr>
                </thead>

<tbody>
                  <tr>
                    <td>IPFS Hash stored on Ethereum</td>
                    <td> : </td>
                    <td>{this.state.ipfsHash}</td>
                  </tr>
                  <tr>
                    <td>Ethereum Contract Address</td>
                    <td> : </td>
                    <td>{this.state.ethAddress}</td>
                  </tr>
                  <tr>
                    <td>Tx # </td>
                    <td> : </td>
                    <td>{this.state.transactionHash}</td>
                  </tr>
                </tbody>
            </table>
        </grid>
     </div>
      <div>
          <h1>Recipient</h1>
          <hr/>
          <br/>
          <p id="accountAddress"></p><br/>
            {/* <form show_cert_2={this.show_cert_2}>
            <Button type="submit">Show Certificate</Button>
            </form> */}
             <Button onClick = {this.show_cert_2}> Show Certificate </Button>
             <table ref="cert_table_1">
               {/* <td>{this.state.issuee_data_1.map(item=>(<li key={item}>{item}</li>))}</td> */}
               {/* {this.createTable()} */}
             </table>

          </div>
          </div>
      );
    }
}
export default App;
